import React from 'react';
import logo from '../assets/Star_Wars_logo.png';
import HomeBigButton from './home-big-button';

const view = (
    <div className="App">
      <h1 className="welcome-title">Bienvenido a tu base de datos de personajes</h1>
      <img className="img-logo" src={logo} />

      <div className="botonera-home">
        <HomeBigButton label="Agregar personaje" ownClass="first-button" value="AGREGAR" onValueChange={this.viewSelectedHandle}/>
        <HomeBigButton label="Eliminar personaje" ownClass="second-button" value="ELIMINAR"/>
        <HomeBigButton label="Obtener personajes" ownClass="third-button" value="OBTENER"/>
      </div>
    </div>
);

class Home extends React.Component {

    constructor(props){
      this.state = {viewSelected: ''};
    }

    viewSelectedHandle(viewSelected){
      this.setState({viewSelected});
    }

    render(){
        return view;
    }
}

export default Home;
