import React from 'react';
import yodaThinking from '../assets/yoda-thinking.webp';
import FormButton from './ui/form-button';
import InputForm from './ui/input-form';

class AgregarCharacter extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            Personaje: '',
            Episodio: ''
        };
    }

    viewSelectedHandle = () => {
        this.props.onViewChange('HOME');
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
    }

    handleChange = (event) => {
        event.preventDefault();
        this.setState({[event.target.name]: event.target.value});
    }

    render(){
        return(
            <div>
                <h1 className="welcome-title">Â¿Agregar un nuevo personaje es lo que deseas, joven padawan?</h1>
                <img className="yoda-thinking" src={yodaThinking} />

                <form id="addForm" className="agregar-character-form" onSubmit={this.handleSubmit.bind(this)}>

                    <InputForm label="Episodio" type="text" onChange={this.handleChange.bind(this)} />

                    <InputForm label="Personaje" type="text" onChange={this.handleChange.bind(this)} />

                    <FormButton id="cancelButton" nameClass="back-button" type="button" label="Volver" onClicked={this.viewSelectedHandle} />
                    <FormButton id="submitButton" nameClass="submit-button" type="submit" label="Guardar" onClicked={null}/>
                </form>
            </div>
        );
    }
}

export default AgregarCharacter;
