import React from 'react';
import yodaThinking from '../assets/yoda-thinking.webp';

class AgregarCharacter extends React.Component {
    render(){
        return(
            <div>
                <h1 class="welcome-title">Â¿Agregar un nuevo personaje es lo que deseas, joven padawan?</h1>
                <img class="yoda-thinking" src={yodaThinking} />

                <form id="addForm" class="agregar-character-form" method="POST">
                    <div class="form-data">
                        <label for="episode">Episodio:</label>
                        <select class="selector" name="episode" id="episode">
                            <option value="">-- Seleccionar un episodio debes --</option>
                            <option value="I">Episodio I</option>
                        </select>
                    </div>

                    <div class="form-data">
                        <label for="character">Personaje:</label>
                        <input class="input" name="character" type="text" />
                    </div>

                    <button id="btnSubmit" class="submit-button">Volver</button>
                    <button id="btnSubmit" class="submit-button" type="submit">Guardar</button>
                </form>
            </div>
        );
    }
}

export default AgregarCharacter;
