import React, { useState, useEffect } from 'react';
import darthVaderCrack from '../assets/Darth_Vader.jpg';
import SelectForm from './ui/select-form';
import FormButton from './ui/form-button';

function EliminarCharacter({changePage}){
    const [episodios, setEpisodios] = useState([]);
    const [personajes, setPersonajes] = useState([]);
    const [episodio, setEpisodio] = useState();
    const [personaje, setPersonaje] = useState('');

    const getEpisodes = async () => {
        try {
            const response = await fetch('http://localhost:8000/getEpisodes', {
                method: 'get',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            if(data.reply){
                return data.reply;
            } else {
                return data.message;
            }
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(async () => {
        let resp = await getEpisodes();
        setEpisodio(resp);
    },[episodios]);

    const handleSubmit = (event) => {
        event.preventDefault();

    }

    const findCharacters = async () => {
        try {
            if(!episodio) return;

            const response = await fetch(`http://localhost:8000/getCharactersByEpisode?episode=${episodio}`, {
                method: 'get',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            if(data.reply){
                setPersonajes(data.reply);
            } else {
                return data.message;
            }
        } catch (err) {
            console.log(err);
        }
    }

    const handleSelectChange = (name, value) => {
        if(name == 'episodio'){
            setEpisodio(value);
            findCharacters();
        } else {
            setPersonaje(value);
        }
    }
    return(
        <div>
            <h1 className="welcome-title">No subestimes el poder de la fuerza</h1>
            <img className="character-float" src={darthVaderCrack} />

            <form id="addForm" className="agregar-character-form" onSubmit={(e)=> handleSubmit(e)} >
                <SelectForm label="Episodio" list={episodios} callback={(event) => handleSelectChange(event.target.name, event.target.value)}></SelectForm>

                <FormButton id="cancelButton" nameClass="back-button" type="button" label="Volver" onClicked={() => changePage('HOME')} />
                <FormButton id="submitButton" nameClass="submit-button" type="submit" label="Guardar"/>
            </form>
        </div>
    );
}

export default EliminarCharacter;
