import React, { useState } from 'react';
import AgregarCharacter from './agregar-character';
import Home from './home';

function Index(){
  const [page, setPage] = useState('HOME');

  const changePage = (pageChanged) => { // the callback. Use a better name
    console.log(pageChanged);
    setPage(pageChanged);
  };

  function renderSwitch(){
    switch(page) {
      case 'HOME':
        return(  <Home changePage={changePage}/> );
      case 'AGREGAR':
        return ( <AgregarCharacter changePage={changePage}/> );
      case 'ELIMINAR':
        return (<h1 className="welcome-title">Se vería el ELIMINAR</h1>);
      case 'OBTENER':
        return (<h1 className="welcome-title">Se vería el OBTENER</h1>);
      default:
        return (<Index />);
    }
  }


  return (renderSwitch());
}

export default Index;
