import React from 'react';
import yodaThinking from '../assets/yoda-thinking.webp';
import FormButton from './form-button';

class AgregarCharacter extends React.Component {

    constructor(props){
        super(props);
    }



    render(){
        return(
            <div>
                <h1 className="welcome-title">Â¿Agregar un nuevo personaje es lo que deseas, joven padawan?</h1>
                <img className="yoda-thinking" src={yodaThinking} />

                <form id="addForm" className="agregar-character-form">
                    <div className="form-data">
                        <label htmlFor="episode">Episodio:</label>
                        <select className="selector" name="episode" id="episode">
                            <option value="">-- Seleccionar un episodio debes --</option>
                            <option value="I">Episodio I</option>
                        </select>
                    </div>

                    <div className="form-data">
                        <label htmlFor="character">Personaje:</label>
                        <input className="input" name="character" type="text" />
                    </div>

                    <FormButton id="cancelButton" nameClass="back-button" type="button" label="Volver" />
                    <FormButton id="submitButton" nameClass="submit-button" type="submit" label="Guardar" />
                </form>
            </div>
        );
    }
}

export default AgregarCharacter;
