import React from 'react';
import logo from '../assets/Star_Wars_logo.png';
import HomeBigButton from './home-big-button';
import {
  BrowserRouter as Router
} from "react-router-dom";

class Home extends React.Component {

    constructor(props){
        super(props);
        this.state = {selected: 'HOME'};
    }

    render(){
        return (
          <Router>
            <div className="App">
              <h1 className="welcome-title">Bienvenido a tu base de datos de personajes</h1>
              <img className="img-logo" src={logo} />

              <div className="botonera-home">
                <HomeBigButton label="Agregar personaje" ownClass="first-button" value="AGREGAR" to="/save-character"/>
                <HomeBigButton label="Eliminar personaje" ownClass="second-button" value="ELIMINAR" to="/save-character"/>
                <HomeBigButton label="Obtener personajes" ownClass="third-button" value="OBTENER" to="/save-character"/>
              </div>
            </div>
          </Router>
        );
    }
}

export default Home;
