import AgregarCharacter from './agregar-character';
import Home from './home';
import {
  Switch,
  Route
} from "react-router-dom";

class Index extends React.Component {

    constructor(props){
      super(props);
      this.state = {viewSelected: 'HOME'};
    }

    viewChangeHandle = (viewSelected) => {
      this.setState({viewSelected});
    }

    render(){
      /*switch (this.state.viewSelected) {
        case 'HOME':
            return(
