          return (
              <h1 className="welcome-title">Se vería el ELIMINAR</h1>
            );
        case 'OBTENER':
          return (
              <h1 className="welcome-title">Se vería el OBTENER</h1>
            );
        default:
          return (<Index />);
      }*/

      <Switch>
          <Route path="/">
            <Home />
          </Route>
          <Route path="/add-character">
            <AgregarCharacter />
          </Route>
          <Route path="/delete-character">
            return <h1 className="welcome-title">Se vería el ELIMINAR</h1>;
          </Route>
          <Route path="/get-characters">
            return <h1 className="welcome-title">Se vería el OBTENER</h1>;
          </Route>
        </Switch>
    }
}

export default Index;
