import React from 'react';
function SelectForm({label, list}){
    return(
        <div className="form-data">
            <label htmlFor={label.toLowerCase()}>{label}: </label>

                <select className="selector" name={label.toLowerCase()}>
                    {list.map((item, index) => <option>{item}</option>)}
                </select>
        </div>
    );
}

export default SelectForm;
