import React from 'react';
import logo from '../assets/Star_Wars_logo.png';
import HomeBigButton from './home-big-button';

class Index extends React.Component {

    constructor(props){
      super(props);
      this.state = {viewSelected: 'HOME'};
    }

    viewSelectedHandle = (viewSelected) => {
      this.setState({viewSelected});
    }

    render(){
      switch (this.state.viewSelected) {
        case 'HOME':
          return (
            <div className="App">
              <h1 className="welcome-title">Bienvenido a tu base de datos de personajes</h1>
              <img className="img-logo" src={logo} />

              <div className="botonera-home">
                <HomeBigButton label="Agregar personaje" ownClass="first-button" value="AGREGAR" onValueChange={this.viewSelectedHandle}/>
                <HomeBigButton label="Eliminar personaje" ownClass="second-button" value="ELIMINAR" onValueChange={this.viewSelectedHandle}/>
                <HomeBigButton label="Obtener personajes" ownClass="third-button" value="OBTENER" onValueChange={this.viewSelectedHandle}/>
              </div>
            </div>);
        case 'AGREGAR':
          return (
              <h1 className="welcome-title">Se ver√≠a el agregar</h1>
            );
        default:
          return (<Home />);
      }
    }
}

export default Index;
