function AgregarCharacter(){
    const [episodio, setEpisodio] = useState('');
    const [personaje, setPersonaje] = useState('');

    async function submitData() {
        if(episodio == '' || personaje == '') return;

        try {
            const response = await fetch('http://localhost:8000/', {
                method: 'get',
                headers: { 'Content-Type': 'application/json' },
                body: {
                    episode: this.state.episodio,
                    character: this.state.personaje
                }
            });
            const data = await response.json();
            console.log(data);
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {submitData()}, [episodio, personaje]);

    fetchData()
    const handleSubmit = (event) => {
        event.preventDefault();
        submitData();
    }

    return (
        <div>
            <h1 className="welcome-title">Â¿Agregar un nuevo personaje es lo que deseas, joven padawan?</h1>
            <img className="yoda-thinking" src={yodaThinking} onSubmit={handleSubmit}/>

            <form id="addForm" className="agregar-character-form" >

                <InputForm label="Episodio" type="text" onChange={(event) => setEpisodio(event.target.value)} />

                <InputForm label="Personaje" type="text" onChange={(event) =>{ setPersonaje(event.target.value)}} />

                <FormButton id="cancelButton" nameClass="back-button" type="button" label="Volver" />
                <FormButton id="submitButton" nameClass="submit-button" type="submit" label="Guardar"/>
            </form>
        </div>
    );
}

export default AgregarCharacter;
